import React, { useState } from "react";

const CreateSurvey = () => {
  const token = localStorage.getItem("token");

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  // 옵션을 배열로 관리
  const [options, setOptions] = useState([""]);
  const [isActive, setIsActive] = useState(true);
  // surveyType 초기값을 빈 문자열로 설정하여 반드시 선택하게 함
  const [surveyType, setSurveyType] = useState("");
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  // 날짜 변환 함수 (ISO 8601 형식, 밀리초 제거)
  const formatDateTime = (date) => {
    return date ? new Date(date).toISOString().split(".")[0] : null;
  };

  // 옵션 변경 처리
  const handleOptionChange = (index, value) => {
    const newOptions = [...options];
    newOptions[index] = value;
    setOptions(newOptions);
  };

  // 옵션 추가
  const addOption = () => {
    setOptions([...options, ""]);
  };

  // 옵션 제거 (최소 하나는 남도록)
  const removeOption = (index) => {
    if (options.length > 1) {
      const newOptions = options.filter((_, i) => i !== index);
      setOptions(newOptions);
    }
  };

  // 설문 데이터 전송
  const handleSubmit = async (e) => {
    e.preventDefault();

    // 옵션 배열을 쉼표로 구분된 문자열로 변환
    const optionsString = options.join(", ");

    const surveyData = {
      title,
      description,
      options: optionsString, // 문자열로 변환하여 전송
      isActive,
      surveyType,
      startDate: formatDateTime(startDate),
      endDate: formatDateTime(endDate),
    };

    try {
      const response = await fetch("http://localhost:8090/api/surveys/addsurvey", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: token ? `Bearer ${token}` : "",
        },
        body: JSON.stringify(surveyData),
      });

      if (response.ok) {
        alert("설문이 성공적으로 추가되었습니다!");
        setTitle("");
        setDescription("");
        setOptions([""]);
        setStartDate(null);
        setEndDate(null);
        setSurveyType("");
      } else {
        throw new Error("설문 추가 실패");
      }
    } catch (error) {
      console.error("설문 추가 중 오류 발생:", error);
      alert("설문 추가 중 오류가 발생했습니다.");
    }
  };

  return (
    <div>
      <h2>설문 추가</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>설문 제목:</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>

        <div>
          <label>설문 설명:</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>

        <div>
          <label>설문 선택지:</label>
          {options.map((option, index) => (
            <div key={index} style={{ display: "flex", alignItems: "center" }}>
              <input
                type="text"
                value={option}
                onChange={(e) => handleOptionChange(index, e.target.value)}
                required
              />
              {options.length > 1 && (
                <button type="button" onClick={() => removeOption(index)}>
                  제거
                </button>
              )}
            </div>
          ))}
          <button type="button" onClick={addOption}>
            선택지 추가
          </button>
        </div>

        <div>
          <label>설문 유형:</label>
          <select
            value={surveyType}
            onChange={(e) => setSurveyType(e.target.value)}
            required
          >
            <option value="" disabled>
              -- 설문 유형 선택 --
            </option>
            <option value="MEAL_SURVEY">급식 만족도</option>
            <option value="ACADEMIC_SURVEY">학교 운영</option>
            <option value="LECTURE_EVALUATION">강의 평가</option>
            <option value="GENERAL_SURVEY">일반 설문</option>
          </select>
        </div>

        <div>
          <label>시작 날짜:</label>
          <input
            type="datetime-local"
            value={startDate || ""}
            onChange={(e) => setStartDate(e.target.value || null)}
          />
        </div>

        <div>
          <label>종료 날짜:</label>
          <input
            type="datetime-local"
            value={endDate || ""}
            onChange={(e) => setEndDate(e.target.value || null)}
          />
        </div>

        <button type="submit">설문 생성</button>
      </form>
    </div>
  );
};

export default CreateSurvey;
