import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import "./StudentDashboard.css";

function StudentDashboard() {
  const [enrollments, setEnrollments] = useState([]); // 수강신청(EnrollmentDTO) 목록
  const [memberId, setMemberId] = useState(null);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [filterStatus, setFilterStatus] = useState("");

  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  // 사용자 정보 가져오기
  useEffect(() => {
    if (!token) {
      setError("로그인이 필요합니다.");
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMemberId(response.data.id);
      } catch (error) {
        console.error("사용자 정보 가져오기 실패:", error);
        alert("사용자 정보를 불러올 수 없습니다. 다시 로그인해주세요.");
        navigate('/login');
      }
    };

    fetchUserInfo();
  }, [token, navigate]);

  // 수강 신청한 강의(Enrollment) 불러오기
  useEffect(() => {
    if (!memberId) return;

    const fetchEnrollments = async () => {
      try {
        const response = await axios.get(`http://localhost:8090/api/enrollments/member/${memberId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        // EnrollmentController에서 EnrollmentDTO 목록을 반환하므로 그대로 사용합니다.
        setEnrollments(response.data);
      } catch (error) {
        console.error("수강 신청한 강의 불러오기 실패:", error);
        setError("수강 신청한 강의를 불러오는 중 오류가 발생했습니다.");
      }
    };

    fetchEnrollments();
  }, [memberId, token]);

  // 검색어와 상태 필터에 따른 강의 목록 필터링
  const filteredEnrollments = enrollments.filter((enrollment) => {
    // EnrollmentDTO에 courseName, courseDescription, courseCredits, status 등의 필드가 있다고 가정합니다.
    const name = (enrollment.courseName || "").toLowerCase();
    const description = (enrollment.courseDescription || "").toLowerCase();
    const matchesSearch =
      name.includes(searchTerm.toLowerCase()) ||
      description.includes(searchTerm.toLowerCase());
    const matchesFilter = filterStatus ? enrollment.status === filterStatus : true;
    return matchesSearch && matchesFilter;
  });

  return (
    <div className="dashboard">
      <main className="main-content">
        <h2>내 수강 강의</h2>
        {error && <p className="error">{error}</p>}
        <input
          type="text"
          placeholder="강좌 검색 (이름 또는 설명)"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="filter-select"
        >
          <option value="">전체 상태</option>
          <option value="PENDING">승인 대기</option>
          <option value="APPROVED">승인됨</option>
          <option value="REJECTED">거절됨</option>
        </select>
        <table>
          <thead>
            <tr>
              <th>강좌명</th>
              <th>승인 상태</th>
              <th>자세히 보기</th>
            </tr>
          </thead>
          <tbody>
            {filteredEnrollments.length > 0 ? (
              filteredEnrollments.map((enrollment) => (
                <tr key={enrollment.courseId}>
                  <td>{enrollment.courseName}</td>
                  <td>{enrollment.status}</td>
                  <td>
                    <Link to={`/courses/${enrollment.courseId}`} className="detail-btn">
                      이동
                    </Link>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" style={{ textAlign: "center" }}>
                  등록된 강좌가 없습니다.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </main>
    </div>
  );
}

export default StudentDashboard;
