// GeneralSurvey.jsx
import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function GeneralSurvey({ onComplete }) {
  const navigate = useNavigate();
  const [groupedSurveys, setGroupedSurveys] = useState({});
  const [selectedSurveyType, setSelectedSurveyType] = useState(null);
  const [responses, setResponses] = useState({});
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [memberId, setMemberId] = useState(null);
  const token = useMemo(() => localStorage.getItem("token"), []);

  const surveyTypeNames = {
    "GENERAL_SURVEY": "Ï†ïÍ∏∞ ÏÑ§Î¨∏Ï°∞ÏÇ¨",
    "MEAL_SURVEY": "Ï†ïÍ∏∞ Í∏âÏãù ÎßåÏ°±ÎèÑ",
    "ACADEMIC_SURVEY": "Ï†ïÍ∏∞ ÌïôÍµê ÏÑ§Î¨∏"
  };

  useEffect(() => {
    if (!token) {
      setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      setLoading(false);
      return;
    }
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMemberId(response.data.id);
      } catch (error) {
        alert("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.");
        navigate('/login');
      }
    };
    fetchUserInfo();
  }, [token, navigate]);

  useEffect(() => {
    if (!token || !memberId) return;
    const fetchSurveys = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/surveys', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const grouped = response.data.reduce((acc, survey) => {
          if (survey.surveyType === "LECTURE_EVALUATION" || survey.surveyType === "TEAM_EVALUATION") {
            return acc;
          }
          if (!acc[survey.surveyType]) {
            acc[survey.surveyType] = [];
          }
          acc[survey.surveyType].push(survey);
          return acc;
        }, {});
        setGroupedSurveys(grouped);
        setLoading(false);
      } catch (error) {
        setError("ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
        setLoading(false);
      }
    };
    fetchSurveys();
  }, [token, memberId]);

  const handleStartSurvey = (surveyType) => {
    setSelectedSurveyType(surveyType);
    setCurrentQuestionIndex(0);
    setResponses({});
  };

  const handleSubmit = async () => {
    // Î™®Îì† ÏßàÎ¨∏Ïóê ÎãµÎ≥ÄÌñàÎäîÏßÄ ÌôïÏù∏: Ï†ÑÏ≤¥ ÏßàÎ¨∏ ÏàòÏôÄ responsesÏóê Ï†ÄÏû•Îêú ÎãµÎ≥Ä Í∞úÏàòÎ•º ÎπÑÍµê
    if (Object.keys(responses).length !== groupedSurveys[selectedSurveyType].length) {
      alert("Î™®Îì† ÏßàÎ¨∏Ïóê ÎãµÎ≥ÄÌï¥Ïïº Ìï©ÎãàÎã§!");
      return;
    }
  
    // Í∞Å ÏßàÎ¨∏ÎßàÎã§ Ï§ëÎ≥µ Ï≤¥ÌÅ¨ ÏàòÌñâ
    for (let [questionId] of Object.entries(responses)) {
      try {
        await axios.post(
          "http://localhost:8090/api/survey-responses/check",
          { surveyId: questionId, memberId: memberId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      } catch (error) {
        if (error.response && error.response.status === 400) {
          alert(error.response.data);
          return;
        } else {
          alert("Ï§ëÎ≥µ Ï≤¥ÌÅ¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
          return;
        }
      }
    }
  
    const requestData = {
      memberId: memberId,
      responses: Object.entries(responses).map(([questionId, response]) => ({
        surveyId: questionId,
        response: response
      }))
    };
  
    try {
      await axios.post("http://localhost:8090/api/survey-responses/submit", requestData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSubmitted(true);
      alert("‚úÖ ÏÑ§Î¨∏Ïù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§!");
      if (onComplete) onComplete();
    } catch (error) {
      alert("ÏÑ§Î¨∏ Ï†úÏ∂ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };
  

  // ÎÇ¥Î∂Ä ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî Ìï®Ïàò: ÏÑ§Î¨∏ ÏÑ†ÌÉù ÌôîÎ©¥ÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
  const handleBack = () => {
    setSelectedSurveyType(null);
    setSubmitted(false);
    setResponses({});
    setCurrentQuestionIndex(0);
  };

  if (loading) return <p>üì° ÏÑ§Î¨∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>;
  if (error) return <p className="error-message">{error}</p>;
  if (submitted) {
    return (
      <div>
        <p>üéâ ÏÑ§Î¨∏Ïù¥ Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§! Í∞êÏÇ¨Ìï©ÎãàÎã§.</p>
        <button onClick={handleBack}>Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</button>
      </div>
    );
  }

  return (
    <div className="survey-container">
      {!selectedSurveyType ? (
        <div>
          <h2>ÏÑ§Î¨∏ Ïú†Ìòï ÏÑ†ÌÉù</h2>
          {Object.keys(groupedSurveys).map((type) => (
            <li key={type}>
              {surveyTypeNames[type] || type}{" "}
              <button onClick={() => handleStartSurvey(type)}>ÏÑ§Î¨∏ ÏãúÏûë</button>
            </li>
          ))}
        </div>
      ) : (
        <div>
          <h2>{surveyTypeNames[selectedSurveyType] || selectedSurveyType} ÏÑ§Î¨∏</h2>
          <p>{groupedSurveys[selectedSurveyType][currentQuestionIndex]?.title}</p>
          <div>
            {groupedSurveys[selectedSurveyType][currentQuestionIndex]?.options.split(',').map(option => (
              <label key={option}>
                <input
                  type="radio"
                  name={`question-${groupedSurveys[selectedSurveyType][currentQuestionIndex].id}`}
                  value={option}
                  checked={responses[groupedSurveys[selectedSurveyType][currentQuestionIndex].id] === option}
                  onChange={() => setResponses(prev => ({ ...prev, [groupedSurveys[selectedSurveyType][currentQuestionIndex].id]: option }))}
                />
                {option}
              </label>
            ))}
          </div>
          <button onClick={() => setCurrentQuestionIndex(prev => Math.max(prev - 1, 0))} disabled={currentQuestionIndex === 0}>Ïù¥Ï†Ñ</button>
          <button onClick={() => setCurrentQuestionIndex(prev => Math.min(prev + 1, groupedSurveys[selectedSurveyType].length - 1))} disabled={currentQuestionIndex === groupedSurveys[selectedSurveyType].length - 1}>Îã§Ïùå</button>
          {currentQuestionIndex === groupedSurveys[selectedSurveyType].length - 1 && <button onClick={handleSubmit}>Ï†úÏ∂ú</button>}
        </div>
      )}
    </div>
  );
}

export default GeneralSurvey;
