import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./ProfessorExamQuestion.css";

const ProfessorExamQuestion = () => {
  const { examId } = useParams();
  const [examTitle, setExamTitle] = useState("");
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [successMessage, setSuccessMessage] = useState("");
  const [selectedQuestionId, setSelectedQuestionId] = useState(null);

  const defaultNewQuestion = {
    questionText: "",
    answers: ["", "", "", ""],
    correctAnswer: "",
    score: "",
    type: "MULTIPLE_CHOICE",
  };

  const [newQuestion, setNewQuestion] = useState(defaultNewQuestion);

  // ‚úÖ ÏãúÌóò Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchExamInfo = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      const response = await axios.get(`http://localhost:8090/api/exams/${examId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data && response.data.title) {
        setExamTitle(response.data.title);
      } else {
        setExamTitle("Ï†úÎ™© ÏóÜÏùå");
      }
    } catch (error) {
      console.error("‚ùå ÏãúÌóò Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error.response?.data || error.message);
      setExamTitle("ÏãúÌóò Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ Î¨∏Ï†ú Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchQuestions = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      const response = await axios.get(`http://localhost:8090/api/exams/${examId}/questions`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (Array.isArray(response.data)) {
        setQuestions(response.data);
      }
    } catch (error) {
      console.error("‚ùå Î¨∏Ï†ú Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error.response?.data || error.message);
      setQuestions([]);
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ Î¨∏Ï†ú ÏÑ†ÌÉùÌïòÏó¨ ÏàòÏ†ï ÌèºÏóê Î°úÎìú
  const handleSelectQuestion = (question) => {
    setSelectedQuestionId(question.id);
    setNewQuestion({
      questionText: question.questionText,
      answers: question.answers || ["", "", "", ""],
      correctAnswer: question.correctAnswer || "",
      score: question.score || "",
      type: question.type || "MULTIPLE_CHOICE",
    });
  };

  // ‚úÖ Î¨∏Ï†ú Ï∂îÍ∞Ä Î∞è ÏàòÏ†ï
  const handleSaveQuestion = async () => {
    let formattedType = newQuestion.type;

    if (!newQuestion.questionText.trim()) {
      alert("Î¨∏Ï†ú ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî.");
      return;
    }

    if (newQuestion.type === "MULTIPLE_CHOICE" && (newQuestion.answers.some(ans => !ans.trim()) || !newQuestion.correctAnswer)) {
      alert("Í∞ùÍ¥ÄÏãù Î¨∏Ï†úÏùò 4Í∞ú ÎãµÏïàÏùÑ Î™®Îëê ÏûÖÎ†•ÌïòÍ≥† Ï†ïÎãµÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî.");
      return;
    }

    if (!newQuestion.score || isNaN(newQuestion.score) || newQuestion.score <= 0) {
      alert("Ïú†Ìö®Ìïú Ï†êÏàòÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.");
      return;
    }

    try {
      const token = localStorage.getItem("token");

      if (selectedQuestionId) {
        // ‚úÖ Í∏∞Ï°¥ Î¨∏Ï†ú ÏàòÏ†ï
        console.log(`üìå [API ÏöîÏ≤≠] Î¨∏Ï†ú ÏàòÏ†ï ÏöîÏ≤≠: ${selectedQuestionId}`);

        await axios.put(`http://localhost:8090/api/exams/${examId}/questions/${selectedQuestionId}`, newQuestion, {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log("‚úÖ Î¨∏Ï†ú ÏàòÏ†ï ÏÑ±Í≥µ!");
        alert("Î¨∏Ï†úÍ∞Ä ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§!"); // ‚úÖ ÏàòÏ†ï ÏôÑÎ£å ÏïåÎ¶º Ï∂îÍ∞Ä
      } else {
        // ‚úÖ ÏÉà Î¨∏Ï†ú Ï∂îÍ∞Ä
        console.log("üìå [API ÏöîÏ≤≠] Î¨∏Ï†ú Ï∂îÍ∞Ä ÏöîÏ≤≠");

        await axios.post(`http://localhost:8090/api/exams/${examId}/questions`, newQuestion, {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log("‚úÖ Î¨∏Ï†ú Ï∂îÍ∞Ä ÏÑ±Í≥µ!");
        alert("Î¨∏Ï†úÍ∞Ä Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§!");
      }

      // ‚úÖ Î¨∏Ï†ú Î™©Î°ù Í∞±Ïã†
      await fetchQuestions();

      // ‚úÖ ÏûÖÎ†• ÌïÑÎìú Ï¥àÍ∏∞Ìôî
      setSelectedQuestionId(null);
      setNewQuestion(defaultNewQuestion);

      setSuccessMessage("Î¨∏Ï†úÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!");
      setTimeout(() => setSuccessMessage(""), 3000);
    } catch (error) {
      console.error("‚ùå Î¨∏Ï†ú Ï†ÄÏû• Ïã§Ìå®:", error.response?.data || error.message);
      alert("Î¨∏Ï†ú Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.");
    }
  };

  // ‚úÖ ÏãúÌóò Îì±Î°ù Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú ÏûÖÎ†• Ìèº Ï¥àÍ∏∞Ìôî
  const handleRegisterExam = () => {
    setSelectedQuestionId(null);
    setNewQuestion(defaultNewQuestion);
  };

  useEffect(() => {
    fetchExamInfo();
    fetchQuestions();
  }, [examId]);

  return (
    <div className="exam-container2">
      {/* ‚úÖ ÏôºÏ™Ω: Î¨∏Ï†ú Î™©Î°ù */}
      <div className="exam-sidebar">
        <h3>Î¨∏Ï†ú Î™©Î°ù</h3>
        {questions.map((question) => (
          <button key={question.id} onClick={() => handleSelectQuestion(question)} className="question-button">
            {question.id}
          </button>
        ))}
        <button onClick={handleRegisterExam} className="register-button">Î¨∏Ï†ú Ï∂îÍ∞Ä</button>
      </div>

      {/* ‚úÖ Ïò§Î•∏Ï™Ω: Î¨∏Ï†ú Ï∂îÍ∞Ä/ÏàòÏ†ï Ìèº */}
      <div className="question-form">
        <h2>{selectedQuestionId ? "Î¨∏Ï†ú ÏàòÏ†ï" : "ÏÉà Î¨∏Ï†ú Ï∂îÍ∞Ä"}</h2>

        {/* ‚úÖ Î¨∏Ï†ú Ïú†Ìòï ÏÑ†ÌÉù (Ï†úÏùº ÏúÑÎ°ú Ïù¥Îèô) */}
        <div className="form-group">
          <label>Î¨∏Ï†ú Ïú†Ìòï</label>
          <div className="question-type-toggle">
            <button 
              className={newQuestion.type === "MULTIPLE_CHOICE" ? "active" : ""}
              onClick={() => setNewQuestion({ ...newQuestion, type: "MULTIPLE_CHOICE", answers: ["", "", "", ""], correctAnswer: "" })}
            >
              Í∞ùÍ¥ÄÏãù (4ÏßÄÏÑ†Îã§)
            </button>
            <button 
              className={newQuestion.type === "ESSAY" ? "active" : ""}
              onClick={() => setNewQuestion({ ...newQuestion, type: "ESSAY", answers: [], correctAnswer: "" })}
            >
              ÏÑúÏà†Ìòï
            </button>
          </div>
        </div>

        <div className="form-group">
