import React, { useState, useEffect, useCallback } from 'react'; // useCallbackÏùÑ import
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StudentEnroll.css';

const StudentEnroll = () => {
  const [courses, setCourses] = useState([]);  
  const [enrolledCourses, setEnrolledCourses] = useState([]); // ‚úÖ Ïã†Ï≤≠Ìïú Í∞ïÏùò Î™©Î°ù
  const [loading, setLoading] = useState(true);  
  const [error, setError] = useState(null);  
  const [memberId, setMemberId] = useState(null); // üî• studentId ‚Üí memberId Î≥ÄÍ≤Ω
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  // üî• useCallbackÏùÑ ÏÇ¨Ïö©Ìï¥ÏÑú fetchEnrollments Ìï®ÏàòÍ∞Ä ÏùòÏ°¥ÏÑ±Ïóê Ï∂îÍ∞ÄÎêòÏßÄ ÏïäÎèÑÎ°ù Ï≤òÎ¶¨
  const fetchEnrollments = useCallback(async () => {
    if (!memberId) return;
    try {
      const response = await axios.get(`http://localhost:8090/api/enrollments/member/${memberId}`, { // üî• Í≤ΩÎ°ú ÏàòÏ†ï
        headers: { Authorization: `Bearer ${token}` } // üî• Î∞±Ìã± ÏÇ¨Ïö©
      });
      setEnrolledCourses(response.data); // ‚úÖ Ïã†Ï≤≠Ìïú Í∞ïÏùò Î™©Î°ù Ï†ÄÏû•
    } catch (error) {
      console.error("‚ùå ÏàòÍ∞ï Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  }, [memberId, token]); // memberIdÏôÄ tokenÏùÑ ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥Ïóê Ï∂îÍ∞Ä

  useEffect(() => {
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate('/login');
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
    
       
        setMemberId(response.data.id);
      } catch (error) {
        console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", error);
        alert("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.");
        navigate('/login');
      }
    };
    

    const fetchCourses = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/courses', {
          headers: { Authorization: `Bearer ${token}` } // üî• Î∞±Ìã± ÏÇ¨Ïö©
        });
        setCourses(response.data);
      } catch (error) {
        setError("Í∞ïÏùò Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
    fetchCourses();
    fetchEnrollments(); // ‚úÖ ÏàòÍ∞ï Ïã†Ï≤≠Ìïú Í∞ïÏùò Í∞ÄÏ†∏Ïò§Í∏∞
  }, [token, navigate, memberId, fetchEnrollments]); // ‚úÖ ÏùòÏ°¥ÏÑ± Î∞∞Ïó¥Ïóê fetchEnrollments Ï∂îÍ∞Ä

  const handleEnroll = async (courseId) => {
    
  
    if (!token || !memberId) { 
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate('/login');
      return;
    }
  
    try {
      await axios.post('http://localhost:8090/api/enrollments', {
        memberId: memberId, // memberId Í∞íÏù¥ nullÏù¥Î©¥ Î∞±ÏóîÎìúÏóêÏÑú Ïò§Î•ò Î∞úÏÉù
        courseId: courseId
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
  
      alert("ÏàòÍ∞ï Ïã†Ï≤≠ ÏôÑÎ£å!");
      fetchEnrollments(); // ‚úÖ Ïã†Ï≤≠ ÌõÑ Îã§Ïãú ÏàòÍ∞ï Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    } catch (error) {
      console.error("‚ùå ÏàòÍ∞ï Ïã†Ï≤≠ Ïã§Ìå®:", error.response?.data || error.message);
      alert("ÏàòÍ∞ï Ïã†Ï≤≠ Ïã§Ìå®: " + (error.response?.data || error.message));
    }
  };
  

  // ÏàòÏ†ïÎêú getEnrollmentStatus Ìï®Ïàò
  const getEnrollmentStatus = (courseId) => {
    // findÎ°ú Ìï¥Îãπ courseIdÏóê ÎßûÎäî ÏàòÍ∞ï ÏÉÅÌÉúÎ•º Ï∞æÍ≥†, ÏïàÏ†ÑÌïòÍ≤å null Ï≤¥ÌÅ¨
    const enrollment = enrolledCourses.find(e => e && e.course && e.course.id === courseId);
    if (enrollment) {
      return enrollment.status; // PENDING, APPROVED, REJECTED
    }
    return null; // ÏóÜÏúºÎ©¥ null Î∞òÌôò
  };

  if (loading) return <div className="loading">Î°úÎî© Ï§ë...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="student-enroll-container">
      <h2>üìö ÏàòÍ∞ï Ïã†Ï≤≠ ÌéòÏù¥ÏßÄ</h2>
      {courses.length === 0 ? (
        <p>ÏàòÍ∞ï Í∞ÄÎä•Ìïú Í∞ïÏùòÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul className="course-list">
          {courses.map((course) => {
            const status = getEnrollmentStatus(course.id);

            return (
              <li key={course.id} className="course-item">
                <h3>{course.name}</h3>
                <p>üìÖ ÌïôÏäµ ÏãúÏûëÏùº: {course.startDate}</p>
                <p>‚è≥ ÌïôÏäµ Ï¢ÖÎ£åÏùº: {course.endDate}</p>
                <p>üîñ ÏÉÅÌÉú: {course.status === 'COMPLETED' ? 'Ïã†Ï≤≠ Í∞ÄÎä•' : 'ÎßàÍ∞êÎê®'}</p>
                
                {status === "PENDING" ? (
                  <p className="pending-text">‚è≥ ÏàòÍ∞ï Ïã†Ï≤≠ Ï§ë</p>
                ) : status === "APPROVED" ? (
                  <p className="approved-text">‚úÖ ÏàòÍ∞ï ÏôÑÎ£å</p>
                ) : (
                  course.status === 'COMPLETED' && (
                    <button className="enroll-btn" onClick={() => handleEnroll(course.id)}>
                      ÏàòÍ∞ï Ïã†Ï≤≠
                    </button>
                  )
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default StudentEnroll;
