import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ProfessorCoursePage.css'; // CSS ÌååÏùº Ï∂îÍ∞Ä

const ProfessorCoursePage = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get('http://localhost:8090/api/courses/professor', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setCourses(response.data);
      } catch (error) {
        console.error('Í∞ïÏùò Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, [token]);

  const handleCourseClick = (courseId) => {
    navigate(`/professor/course/${courseId}`);
  };

  if (loading) {
    return <div className="lms-loading">Í∞ïÏùò Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</div>;
  }

  return (
    <div className="lms-container">
      <h1 className="lms-title">üìö ÎÇ¥ Í∞ïÏùò Î™©Î°ù</h1>
      <p className="lms-subtitle">ÌòÑÏû¨ Îã¥ÎãπÌïòÍ≥† Í≥ÑÏã† Í∞ïÏùò Î™©Î°ùÏûÖÎãàÎã§.</p>

      <div className="lms-course-list">
  {courses.filter(course => course.status === "COMPLETED").length === 0 ? (
    <p className="lms-no-courses">ÌòÑÏû¨ ÏäπÏù∏Îêú Í∞ïÏùòÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
  ) : (
    courses
      .filter(course => course.status === "COMPLETED") // ‚úÖ ÏäπÏù∏Îêú Í∞ïÏùòÎßå ÌïÑÌÑ∞ÎßÅ
      .map((course) => (
        <div key={course.id} className="lms-course-card" onClick={() => handleCourseClick(course.id)}>
          <div className="lms-course-header">
            <h2 className="lms-course-name">{course.name}</h2>
            <span className="lms-course-status approved">ÏäπÏù∏Îê®</span> {/* ‚úÖ ÏäπÏù∏Îêú Í∞ïÏùò ÌëúÏãú */}
          </div>
          <p className="lms-course-type">üìå Í≥ºÎ™© Ïú†Ìòï: {course.courseType || 'ÎØ∏ÏßÄÏ†ï'}</p>
          <p className="lms-course-professor">üë®‚Äçüè´ Îã¥Îãπ ÍµêÏàò: {course.professor || 'ÎØ∏ÏßÄÏ†ï'}</p>
          <button 
            className="lms-enter-btn"
            onClick={(e) => {
              e.stopPropagation();
              handleCourseClick(course.id);
            }}
          >
            Í∞ïÏùòÏã§ ÏûÖÏû• 
          </button>
        </div>
      ))
  )}
</div>

    </div>
  );
};

export default ProfessorCoursePage;
