import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom"; // üîπ useNavigate Ï∂îÍ∞Ä
import axios from "axios";

const ExamDetail = () => {
  const { examId } = useParams();
  const navigate = useNavigate(); // üîπ useNavigate ÌõÖ ÏÇ¨Ïö©
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [essayAnswers, setEssayAnswers] = useState([]);
  const [scores, setScores] = useState({});
  const [loading, setLoading] = useState(true);
  const userRole = localStorage.getItem("role");

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
          return;
        }

        const studentsResponse = await axios.get(
          `http://localhost:8090/api/exams/${examId}/submitted-students`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setStudents(studentsResponse.data);
      } catch (error) {
        console.error("ÌïôÏÉù Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù", error);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, [examId]);

  const fetchStudentAnswers = async (studentId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        return;
      }

      const response = await axios.get(
        `http://localhost:8090/api/essay-questions/exam/${examId}/answers`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const filteredAnswers = response.data.filter(answer => answer.studentId === studentId);
      setEssayAnswers(filteredAnswers);
      setSelectedStudent(students.find(student => student.id === studentId));

      const initialScores = {};
      filteredAnswers.forEach(answer => {
        initialScores[answer.questionId] = answer.score || "";
      });
      setScores(initialScores);
    } catch (error) {
      console.error("ÌïôÏÉù ÎãµÎ≥ÄÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù", error);
    }
  };

  const handleScoreChange = (questionId, value) => {
    setScores({ ...scores, [questionId]: value });
  };

  const handleSaveScores = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        return;
      }

      const requests = Object.entries(scores).map(([questionId, score]) =>
        axios.put(
          `http://localhost:8090/api/essay-questions/score`,
          {
            studentId: selectedStudent.id,
            questionId: parseInt(questionId, 10),
            score: parseInt(score, 10),
          },
          { headers: { Authorization: `Bearer ${token}` } }
        )
      );

      await Promise.all(requests);
      alert("Ï†êÏàòÍ∞Ä Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§.");
    } catch (error) {
      console.error("Ï†êÏàò Ï†ÄÏû• Ï§ë Ïò§Î•ò Î∞úÏÉù", error);
      alert("Ï†êÏàò Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  if (loading) {
    return <p>Î°úÎî© Ï§ë...</p>;
  }

  return (
    <div>
      <h1>ÏãúÌóò ÏÉÅÏÑ∏ Ï†ïÎ≥¥</h1>

      {/* üîπ ÍµêÏàòÏùº Í≤ΩÏö∞ Ï±ÑÏ†ê ÌéòÏù¥ÏßÄ ÌëúÏãú */}
      {userRole === "PROFESSOR" && (
        <div>
          <h2>ÌïôÏÉù Î™©Î°ù</h2>
          <ul>
            {students.map(student => (
              <li key={student.id} onClick={() => fetchStudentAnswers(student.id)} style={{ cursor: "pointer", color: "blue" }}>
                {student.name} - Ï¥ùÏ†ê: {student.score}
              </li>
            ))}
          </ul>

          {selectedStudent && (
            <div>
              <h2>{selectedStudent.name}Ïùò ÏÑúÏà†Ìòï ÎãµÎ≥Ä</h2>
              {essayAnswers.length > 0 ? (
                <div>
                  {essayAnswers.map(answer => (
                    <div key={answer.questionId} style={{ marginBottom: "20px" }}>
                      <p><strong>Î¨∏Ï†ú:</strong> {answer.questionText}</p>
                      <p><strong>Ï†úÏ∂ú ÎãµÏïà:</strong> {answer.answerText}</p>
                      <input
                        type="number"
                        value={scores[answer.questionId]}
                        onChange={(e) => handleScoreChange(answer.questionId, e.target.value)}
                        placeholder="Ï†êÏàò ÏûÖÎ†•"
                      />
                    </div>
                  ))}
                  <button onClick={handleSaveScores}>ÏôÑÎ£å</button>
                </div>
              ) : (
                <p>ÏÑúÏà†Ìòï ÎãµÎ≥ÄÏù¥ ÏóÜÏäµÎãàÎã§.</p>
              )}
            </div>
          )}
        </div>
      )}

      {/* üîπ ÌïôÏÉùÏùº Í≤ΩÏö∞ "ÏùëÏãúÌïòÍ∏∞" Î≤ÑÌäº ÌëúÏãú */}
      {userRole === "STUDENT" && (
        <div>
          <p>Ïù¥ ÏãúÌóòÏùÑ ÏùëÏãúÌïòÎ†§Î©¥ ÏïÑÎûò Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠ÌïòÏÑ∏Ïöî.</p>
          <button onClick={() => navigate(`/exam/${examId}/details`)}>ÏùëÏãúÌïòÍ∏∞</button>
        </div>
      )}
    </div>
  );
};

export default ExamDetail;
