import React, { useCallback, useState, useEffect } from 'react';
import { useNavigate, Outlet } from 'react-router-dom';
import axios from 'axios';
import './StudentDashboard.css';
import StudentSidebar from './StudentSidebar';

const StudentDashboard = () => {
  const navigate = useNavigate();
  
  const [user, setUser] = useState(null);
  const [courses, setCourses] = useState([]);
  const [progress, setProgress] = useState(0);
  const [attendance, setAttendance] = useState("X");
  const [score, setScore] = useState(0);
  const [notices, setNotices] = useState([]);
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);

  // ✅ 로컬 스토리지에서 studentId와 role을 가져와 user 객체 구성
  useEffect(() => {
    const storedStudentId = localStorage.getItem("studentId");
    const storedRole = localStorage.getItem("role");
    if (storedStudentId && storedRole) {
      setUser({ studentId: storedStudentId, role: storedRole });
    } else {
      setUser(null);
    }
  }, []);

  // ✅ 학생의 강의 등록 정보 조회
  const fetchCourses = useCallback(async () => {
    if (!user?.studentId) return;
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(
        `http://localhost:8090/api/students/enrollments/${user.studentId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCourses(response.data);
    } catch (error) {
      console.error("⚠️ 강의 데이터를 불러오는데 실패했습니다.", error);
    }
  }, [user]);

  // ✅ 공지사항 조회
  const fetchNotices = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:8090/api/students/notices", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotices(response.data);
    } catch (error) {
      console.error("⚠️ 공지사항 데이터를 불러오는데 실패했습니다.", error);
    }
  };

  // ✅ 학습 상태 조회
  const fetchLearningStatus = useCallback(async () => {
    if (!user?.studentId) return;
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(
        `http://localhost:8090/api/students/learning-status/${user.studentId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const studentStatus = response.data;
      if (!studentStatus.courses?.length) return;
      const totalProgress = studentStatus.courses.reduce((sum, course) => sum + course.progress, 0);
      setProgress(Math.round(totalProgress / studentStatus.courses.length));
      const attendedClasses = studentStatus.courses.filter(course => course.attendance === "O").length;
      setAttendance(`${attendedClasses} / ${studentStatus.courses.length}`);
      const totalScore = studentStatus.courses.reduce((sum, course) => sum + course.score, 0);
      setScore(Math.round(totalScore / studentStatus.courses.length));
    } catch (error) {
      console.error("⚠️ 학습 상태 데이터를 불러오는데 실패했습니다.", error);
    }
  }, [user]);

  // ✅ 최근 활동 조회
  const fetchRecentActivity = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:8090/api/students/recent-activity", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRecentActivity(response.data);
    } catch (error) {
      console.error("⚠️ 최근 활동 데이터를 불러오는데 실패했습니다.", error);
    }
  };

  // ✅ 데이터 불러오기
  useEffect(() => {
    if (user && user.studentId) {
      Promise.all([
        fetchCourses(),
        fetchNotices(),
        fetchLearningStatus(),
        fetchRecentActivity()
      ]).then(() => setLoading(false));
    }
  }, [fetchCourses, fetchLearningStatus, user]);

  const markAsRead = (id) => {
    setNotices(notices.map(n => (n.id === id ? { ...n, read: true } : n)));
  };

  if (!user) return <div>로그인 정보를 불러오는 중...</div>;
  if (loading) return <div>데이터 불러오는 중...</div>;

  return (
    <div className="dashboard">
      <StudentSidebar />
      <main className="main-content">
        <Outlet />

        {/* ✅ 레이아웃이 깨지지 않도록 `page-content` 추가 */}
        <div className="page-content">
          
          <div className="info-section">
            {/* 공지사항 */}
            <section className="notice-section">
              <h3>📢 공지사항</h3>
              <ul className="notice-list">
                {notices.length ? (
                  notices.map(notice => (
                    <li key={notice.id}
                        className="notice-item"
                        onClick={() => navigate(`/notices/${notice.id}`)}
                        style={{ cursor: "pointer", textDecoration: "underline" }}>
                      📌 {notice.title} ({notice.date})
                    </li>
                  ))
                ) : (
                  <li>공지사항이 없습니다.</li>
                )}
              </ul>
            </section>

            {/* 강의 목록 */}
            <section className="course-section">
              <h3>🎓 등록된 강의 목록</h3>
              <ul className="course-list">
                {courses.length ? (
                  courses.map(course => (
                    <li key={course.course_id} className="course-item">
                      <button onClick={() => navigate(`/student/courses/${course.course_id}`)}>
                        📖 {course.name} | ⏳ {course.duration || "N/A"}분
                      </button>
                    </li>
                  ))
                ) : (
                  <li>등록된 강의가 없습니다.</li>
                )}
              </ul>
            </section>
          </div>

          {/* ✅ `status-row` 안 깨지도록 수정 */}
          <div className="status-row">
            <section className="progress-section">
              <h3>📊 학습 상태</h3>
              <div className="progress-bar">
                <div className="progress" style={{ width: `${progress}%` }}>{progress}%</div>
              </div>
              <div className="attendance-score">
                <span>출석률: {attendance}</span>
                <span>📈 평균 성적: {score}점</span>
              </div>
            </section>

            <section className="recent-activity-section">
              <h3>🕒 최근 활동</h3>
              <ul>
                {recentActivity.length ? (
                  recentActivity.slice(-5).map((activity, index) => (
                    <li key={index}>🔹 {activity.activity}</li>
                  ))
                ) : (
                  <li>최근 활동이 없습니다.</li>
                )}
              </ul>
            </section>

            <section className="notification-section">
              <h3>🔔 알림</h3>
              <ul>
                {notices.length ? (
                  notices.map(notice => (
                    <li key={notice.id}
                        className={`notification-item ${notice.read ? "read" : "unread"}`}
                        onClick={() => markAsRead(notice.id)}>
                      {notice.message}
                    </li>
                  ))
                ) : (
                  <li>새로운 알림이 없습니다.</li>
                )}
              </ul>
            </section>
          </div>

        </div>
      </main>
    </div>
  );
};

export default StudentDashboard;
