import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import "./AssignmentDetail.css"; // Ï∂îÍ∞ÄÎêú CSS ÌååÏùº

const AssignmentDetail = () => {
    const { courseId, weekNumber } = useParams();
    const [assignments, setAssignments] = useState([]);
    const [courseName, setCourseName] = useState("");
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [selectedFile, setSelectedFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [isProfessor, setIsProfessor] = useState(false);
    const [isStudent, setIsStudent] = useState(false);

    const token = localStorage.getItem('token');
    const studentId = localStorage.getItem('studentId'); // Î°úÍ∑∏Ïù∏ Ïãú Ï†ÄÏû•Ìïú ÌïôÎ≤à

    useEffect(() => {
        const fetchData = async () => {
            if (!token) {
                setError('Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.');
                setLoading(false);
                return;
            }
            try {
                // Í≥ºÏ†ú Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
                const assignmentsResponse = await axios.get(
                    `http://localhost:8090/api/courses/${courseId}/weeks/${weekNumber}/assignments`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                setAssignments(assignmentsResponse.data);

                // Í∞ïÏ¢å Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞
                const courseResponse = await axios.get(
                    `http://localhost:8090/api/courses/${courseId}`,
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                setCourseName(courseResponse.data.name);

                // ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥(Ïó≠Ìï†) ÌôïÏù∏
                const userResponse = await axios.get(
                    'http://localhost:8090/api/users/me',
                    { headers: { Authorization: `Bearer ${token}` } }
                );
                if (userResponse.data.role === 'PROFESSOR') {
                    setIsProfessor(true);
                } else if (userResponse.data.role === 'STUDENT') {
                    setIsStudent(true);
                }
            } catch (err) {
                console.error('Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù:', err);
                setError('Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [courseId, weekNumber, token]);

    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
    };

    // üî• ÎßàÍ∞êÏùº Ï≤¥ÌÅ¨Îßå ÏàòÌñâ
    const handleSubmitAssignment = async (assignmentId, dueDate) => {
        const today = new Date();
        const assignmentDueDate = new Date(dueDate);

        // üî• ÎßàÍ∞êÏùº Ï≤¥ÌÅ¨ (ÎßàÍ∞êÏùºÏù¥ ÏßÄÎÇ¨ÏúºÎ©¥ Ï†úÏ∂ú Î∂àÍ∞Ä)
        if (today > assignmentDueDate) {
            alert('Í≥ºÏ†ú Ï†úÏ∂ú ÎßàÍ∞êÏùºÏù¥ ÏßÄÎÇ¨ÏäµÎãàÎã§. Ï†úÏ∂úÌï† Ïàò ÏóÜÏäµÎãàÎã§.');
            return;
        }

        if (!selectedFile) {
            alert('ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
            return;
        }

        setUploading(true);
        const formData = new FormData();
        formData.append('file', selectedFile);

        try {
            await axios.post(
                `http://localhost:8090/api/submissions/${assignmentId}/submit`,
                formData,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'multipart/form-data',
                        studentId: studentId // Ìó§ÎçîÏóê ÌïôÎ≤à Ï†ÑÎã¨
                    }
                }
            );
            alert('Í≥ºÏ†úÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†úÏ∂úÎêòÏóàÏäµÎãàÎã§!');
        } catch (err) {
            console.error('Í≥ºÏ†ú Ï†úÏ∂ú Ïã§Ìå®:', err);
            alert('Í≥ºÏ†ú Ï†úÏ∂úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
        } finally {
            setUploading(false);
            setSelectedFile(null);
        }
    };

    if (loading) return <div>Î°úÎî© Ï§ë...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div className="assignment-detail-container">
            <h2 className="assignment-detail-header">
                {courseName} Í∞ïÏ¢åÏùò {weekNumber}Ï£ºÏ∞® Í≥ºÏ†ú
            </h2>
            
            {assignments.length > 0 ? (
                <ul className="assignment-detail-list">
                    {assignments.map((assignment) => {
                        // ÎßàÍ∞êÏùº Î≥ÄÌôò
                        const formattedDueDate = assignment.dueDate 
                            ? new Date(assignment.dueDate).toLocaleString("ko-KR", { timeZone: "Asia/Seoul" }) 
                            : "ÎßàÍ∞êÏùº Ï†ïÎ≥¥ ÏóÜÏùå";

                        // ÌòÑÏû¨ ÎÇ†ÏßúÏôÄ ÎπÑÍµêÌïòÏó¨ Ï†úÏ∂ú Í∞ÄÎä• Ïó¨Î∂Ä ÌôïÏù∏
                        const canSubmit = new Date() <= new Date(assignment.dueDate);

                        return (
                            <li className="assignment-detail-item" key={assignment.id}>
                                <p>Ï†úÎ™©: {assignment.title}</p>
                                <p>ÏÑ§Î™Ö: {assignment.description}</p>
                                <p>ÎßàÍ∞êÏùº: {formattedDueDate}</p>

                                {isProfessor && (
                                    <div className="assignment-actions">
                                        <Link 
                                            to={`/api/courses/${courseId}/weeks/${weekNumber}/assignments/${assignment.id}/grade`}
                                        >
                                            <button>Í≥ºÏ†ú ÌèâÍ∞ÄÌïòÍ∏∞</button>
                                        </Link>
                                    </div>
                                )}

                                {isStudent && (
                                    <div className="assignment-actions">
                                        <input
                                            className="assignment-file-input"
                                            type="file"
                                            onChange={handleFileChange}
                                        />
                                        {!canSubmit ? (
                                            <p style={{ color: 'red' }}>
                                                Í≥ºÏ†ú Ï†úÏ∂ú ÎßàÍ∞êÏùºÏù¥ ÏßÄÎÇ¨ÏäµÎãàÎã§. Ï†úÏ∂úÌï† Ïàò ÏóÜÏäµÎãàÎã§.
                                            </p>
                                        ) : (
                                            <button 
                                                onClick={() => handleSubmitAssignment(assignment.id, assignment.dueDate)}
                                                disabled={uploading}
                                            >
                                                {uploading ? 'Ï†úÏ∂ú Ï§ë...' : 'Í≥ºÏ†ú Ï†úÏ∂ú'}
                                            </button>
                                        )}
                                    </div>
                                )}
                            </li>
                        );
                    })}
                </ul>
            ) : (
                <p>Ìï¥Îãπ Ï£ºÏ∞®Ïóê Í≥ºÏ†úÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
            )}
        </div>
    );
};

export default AssignmentDetail;
