import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import './TeamProjectHistory.css';

function TeamProjectHistory() {
  const [studentInfo, setStudentInfo] = useState(null);
  const [teamId, setTeamId] = useState(null);
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");

  // ‚úÖ `submissions` Îç∞Ïù¥ÌÑ∞ÏóêÏÑú `teamId` Ï∞æÎäî Î∞©Ïãù ÏàòÏ†ï
  const filteredSubmissions = useMemo(() => {
    return Array.isArray(submissions)
      ? submissions.filter(submission => {
          // submission.teamProject.team.id ÎåÄÏã† submission.teamProject.id ÎπÑÍµê
          return submission.teamProject?.id === teamId;
        })
      : [];
  }, [submissions, teamId]);
  
  useEffect(() => {
    if (!token) {
      console.warn("‚ö†Ô∏è ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§. Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      setError("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      setLoading(false);
      return;
    }

    const fetchStudentData = async () => {
      try {
        // 1Ô∏è‚É£ Î°úÍ∑∏Ïù∏Ìïú ÌïôÏÉù Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
        const userResponse = await axios.get("http://localhost:8090/api/users/me", {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (!userResponse.data || !userResponse.data.id) {
          console.warn("‚ö†Ô∏è ÌïôÏÉù Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          setError("ÌïôÏÉù Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          return;
        }
        const studentId = userResponse.data.id;
        setStudentInfo(userResponse.data);

        // 2Ô∏è‚É£ ÌåÄÏõê Î™©Î°ùÏóêÏÑú ÌïôÏÉùÏù¥ ÏÜçÌïú ÌåÄ ID Ï∞æÍ∏∞
        const teamMembersResponse = await axios.get("http://localhost:8090/api/team-members", {
          headers: { Authorization: `Bearer ${token}` }
        });

        console.log("üìå ÌåÄÏõê Î™©Î°ù ÏùëÎãµ:", teamMembersResponse.data);

        const studentTeam = teamMembersResponse.data.find(member => member.member_id === studentId);

        if (!studentTeam || !studentTeam.team_id) {
          console.warn("‚ö†Ô∏è ÌïôÏÉùÏù¥ ÏÜçÌïú ÌåÄÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          setError("ÌïôÏÉùÏù¥ ÏÜçÌïú ÌåÄÏù¥ ÏóÜÏäµÎãàÎã§.");
          return;
        }

        const studentTeamId = studentTeam.team_id;
        console.log(`‚úÖ ÌïôÏÉùÏù¥ ÏÜçÌïú ÌåÄ ID: ${studentTeamId}`);
        setTeamId(studentTeamId);

        // 3Ô∏è‚É£ Ìï¥Îãπ ÌåÄÏùò ÌîÑÎ°úÏ†ùÌä∏ Ï†úÏ∂ú ÎÇ¥Ïó≠ Í∞ÄÏ†∏Ïò§Í∏∞
        const submissionsResponse = await axios.get("http://localhost:8090/api/team-project-submissions", {
          headers: { Authorization: `Bearer ${token}` },
          params: { teamId: studentTeamId }
        });

        console.log("üìÇ Ï†úÏ∂ú ÎÇ¥Ïó≠ ÏùëÎãµ:", submissionsResponse.data);

        if (!submissionsResponse.data || !Array.isArray(submissionsResponse.data)) {
          console.warn("‚ö†Ô∏è Ï†úÏ∂ú ÎÇ¥Ïó≠Ïù¥ ÏóÜÏäµÎãàÎã§.");
          setError("Ïù¥ ÌåÄÏóêÏÑú Ï†úÏ∂úÌïú ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.");
          return;
        }

        setSubmissions(submissionsResponse.data);
      } catch (error) {
        console.error("‚ö†Ô∏è Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®:", error);
        setError("Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      } finally {
        setLoading(false);
      }
    };

    fetchStudentData();
  }, [token]);

  if (loading) {
    return (
      <div className="team-project-history">
        <h2>ÌåÄ ÌîÑÎ°úÏ†ùÌä∏ Ï†úÏ∂ú ÎÇ¥Ïó≠</h2>
        <p>üì° Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
      </div>
    );
  }

  return (
    <div className="team-project-history">
      <h2>ÌåÄ ÌîÑÎ°úÏ†ùÌä∏ Ï†úÏ∂ú ÎÇ¥Ïó≠</h2>
      {error ? (
        <p className="error-message">{error}</p>
      ) : filteredSubmissions.length === 0 ? (
        <p>Ï†úÏ∂úÎêú ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <table className="history-table">
          <thead>
            <tr>
              <th>ÌåÄÎ™Ö</th>
              <th>Ï†úÏ∂ú ÌååÏùº</th>
              <th>Ï†úÏ∂ú ÏãúÍ∞Ñ</th>
            </tr>
          </thead>
          <tbody>
            {filteredSubmissions.map((submission, index) => (
              <tr key={`${submission.id}-${index}`}>
                <td>{submission.teamProject?.team?.name || 'ÌåÄÎ™Ö ÎØ∏ÏßÄÏ†ï'}</td>
                <td>
                  {submission.files ? (
                    <a href={`/uploads/${submission.files}`} download>
                      {submission.files}
                    </a>
                  ) : (
                    <span>ÌååÏùº ÏóÜÏùå</span>
                  )}
                </td>
                <td>{new Date(submission.submittedAt).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default TeamProjectHistory;
