import React, { useState, useEffect } from "react";
import axios from "axios";

const AddAnnouncementForm = ({ courseId, weekNumber }) => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [memberId, setMemberId] = useState(null); // üîπ memberId ÏÉÅÌÉú Ï∂îÍ∞Ä
  const token = localStorage.getItem("token");

  // üîπ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    if (token) {
      const fetchUserData = async () => {
        try {
          const userResponse = await axios.get("http://localhost:8090/api/users/me", {
            headers: { Authorization: `Bearer ${token}` },
          });
          setMemberId(userResponse.data.id); // ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÏùò memberId Ï†ÄÏû•
        } catch (error) {
          console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå®:", error);
          setMessage("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
        }
      };

      fetchUserData();
    }
  }, [token]); // üîπ tokenÏù¥ Î≥ÄÍ≤ΩÎê† Îïå Ïã§Ìñâ

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    if (!title.trim() || !content.trim()) {
      setMessage("‚ö†Ô∏è Ï†úÎ™©Í≥º ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
      setLoading(false);
      return;
    }

    if (!token) {
      setMessage("‚ùå Î°úÍ∑∏Ïù∏ ÌõÑ Ïù¥Ïö©Ìï¥Ï£ºÏÑ∏Ïöî.");
      setLoading(false);
      return;
    }

    if (!memberId) {
      setMessage("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.");
      setLoading(false);
      return;
    }

    try {
      // üîπ API ÏöîÏ≤≠ (ÏøºÎ¶¨ ÌååÎùºÎØ∏ÌÑ∞Î°ú memberId Ï∂îÍ∞Ä)
      await axios.post(
        `http://localhost:8090/api/courses/${courseId}/weeks/${weekNumber}/announcements?memberId=${memberId}`,
        { title, content },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage("‚úÖ Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ Îì±Î°ùÎêòÏóàÏäµÎãàÎã§.");
      setTitle("");
      setContent("");
    } catch (error) {
      console.error("üì¢ Í≥µÏßÄÏÇ¨Ìï≠ ÏóÖÎ°úÎìú Ïã§Ìå®:", error);
      setMessage("‚ùå Í≥µÏßÄÏÇ¨Ìï≠ Ï∂îÍ∞Ä Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "0 auto", padding: "20px", border: "1px solid #ccc", borderRadius: "10px", background: "#fff" }}>
      <h2>üì¢ Í≥µÏßÄÏÇ¨Ìï≠ Ï∂îÍ∞Ä</h2>
      {message && <p style={{ color: message.startsWith("‚úÖ") ? "green" : "red" }}>{message}</p>}
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "10px" }}>
          <label>Ï†úÎ™©</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label>ÎÇ¥Ïö©</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            rows="4"
            style={{ width: "100%", padding: "8px", borderRadius: "5px", border: "1px solid #ccc" }}
          />
        </div>
        <button type="submit" disabled={loading || !memberId} style={{ width: "100%", padding: "10px", background: "#007bff", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" }}>
          {loading ? "Îì±Î°ù Ï§ë..." : "Í≥µÏßÄ Ï∂îÍ∞Ä"}
        </button>
      </form>
    </div>
  );
};

export default AddAnnouncementForm;
