import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './StudentAttendance.css';

const StudentAttendance = () => {
  const { courseId } = useParams();
  const [attendanceData, setAttendanceData] = useState([]);
  const [courseName, setCourseName] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('token');
  const studentId = localStorage.getItem('studentId');

  useEffect(() => {
    const fetchCourseDetails = async () => {
      try {
        // 강의 정보 가져오기
        const courseResponse = await axios.get(
          `http://localhost:8090/api/courses/${courseId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setCourseName(courseResponse.data.name);
        
        // 출석 정보 가져오기
        const attendanceResponse = await axios.get(
          `http://localhost:8090/api/attendance/course/${courseId}?studentId=${studentId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        
        setAttendanceData(attendanceResponse.data);
        setLoading(false);
      } catch (error) {
        console.error('❌ 출석 정보 불러오기 오류:', error);
        setError('출석 정보를 불러오는 중 오류가 발생했습니다.');
        setLoading(false);
      }
    };

    if (courseId && token && studentId) {
      fetchCourseDetails();
    } else {
      setError('필요한 정보가 없습니다. 로그인 상태를 확인해주세요.');
      setLoading(false);
    }
  }, [courseId, token, studentId]);

  // 주차별 출석 데이터 생성 (API 응답 형식에 따라 수정 필요)
  const generateWeeklyAttendance = () => {
    // 실제 API 응답 형식으로 데이터를 가공
    // 여기서는 예시로 출석 데이터 가공 로직 구현
    
    // 주차 생성 (1~15주차)
    const weeks = Array.from({ length: 15 }, (_, i) => i + 1);
    
    return weeks.map(weekNum => {
      // API 응답에서 해당 주차 데이터 찾기
      const weekData = attendanceData.find(item => item.weekNumber === weekNum) || {};
      
      return {
        weekNumber: weekNum,
        // 출석 상태: PRESENT(출석), ABSENT(결석), NOT_YET(아직 안함)
        status: weekData.status || 'NOT_YET',
        // 진도율 (퍼센트)
        progressRate: weekData.progressRate || 0,
        // 출석 날짜
        attendanceDate: weekData.attendanceDate || null
      };
    });
  };

  const getStatusLabel = (status) => {
    switch(status) {
      case 'PRESENT': return '출석';
      case 'ABSENT': return '결석';
      case 'NOT_YET': return '미수강';
      default: return '미수강';
    }
  };

  const getStatusClass = (status) => {
    switch(status) {
      case 'PRESENT': return 'status-present';
      case 'ABSENT': return 'status-absent';
      case 'NOT_YET': return 'status-notyet';
      default: return 'status-notyet';
    }
  };

  if (loading) {
    return <div className="loading">로딩 중...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  const weeklyAttendance = generateWeeklyAttendance();

  return (
    <div className="attendance-container">
      <div className="attendance-header">
        <h2>{courseName} - 출석 현황</h2>
      </div>
      
      <div className="attendance-summary">
        <p>전체 출석률: {calculateAttendanceRate(weeklyAttendance)}%</p>
        <p>출석: {countStatus(weeklyAttendance, 'PRESENT')}회, 
           결석: {countStatus(weeklyAttendance, 'ABSENT')}회, 
           미수강: {countStatus(weeklyAttendance, 'NOT_YET')}회</p>
      </div>
      
      <div className="attendance-table-container">
        <table className="attendance-table">
          <thead>
            <tr>
              <th>주차</th>
              <th>상태</th>
              <th>진도율</th>
              <th>출석일시</th>
            </tr>
          </thead>
          <tbody>
            {weeklyAttendance.map((week) => (
              <tr key={`week-${week.weekNumber}`}>
                <td>{week.weekNumber}주차</td>
                <td>
                  <span className={`status-badge ${getStatusClass(week.status)}`}>
                    {getStatusLabel(week.status)}
                  </span>
                </td>
                <td>
                  <div className="progress-bar-container">
                    <div 
                      className="progress-bar" 
                      style={{ width: `${week.progressRate}%` }}
                    ></div>
                    <span className="progress-text">{week.progressRate.toFixed(1)}%</span>
                  </div>
                </td>
                <td>
                  {week.attendanceDate 
                    ? new Date(week.attendanceDate).toLocaleString('ko-KR')
                    : '-'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// 출석률 계산 함수
const calculateAttendanceRate = (weeklyData) => {
  const totalWeeks = weeklyData.length;
  const presentCount = weeklyData.filter(week => week.status === 'PRESENT').length;
  return totalWeeks > 0 ? ((presentCount / totalWeeks) * 100).toFixed(1) : 0;
};

// 특정 상태 카운트 함수
const countStatus = (weeklyData, status) => {
  return weeklyData.filter(week => week.status === status).length;
};

export default StudentAttendance;
