import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid'; // ‚úÖ UUID Ï∂îÍ∞Ä
import './MessagePage.css';

const socket = io('http://localhost:5000');

const MessagePage = () => {
  const [member, setMember] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [recipient, setRecipient] = useState(null);
  const [members, setMembers] = useState([]);

  // ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∞è Ï†ÑÏ≤¥ Î©§Î≤Ñ Ï°∞Ìöå
  useEffect(() => {
    async function fetchMemberData() {
      try {
        const token = localStorage.getItem("token");
        const resMe = await axios.get('http://localhost:8090/api/users/me', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMember(resMe.data);

        const resMembers = await axios.get('http://localhost:8090/api/chat-members', {
          headers: { Authorization: `Bearer ${token}` }
        });

        const otherMembers = resMembers.data.filter(m => m.studentId !== resMe.data.studentId);
        setMembers(otherMembers);
      } catch (error) {
        console.error("üö® Î©§Î≤Ñ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.", error);
      }
    }
    fetchMemberData();
  }, []);

  // ‚úÖ Î∞±ÏóîÎìúÏóêÏÑú Ï±ÑÌåÖ ÎÇ¥Ïó≠ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    async function fetchMessages() {
      if (!member || !recipient) return;

      try {
        const token = localStorage.getItem("token");
        if (!token) {
          console.error("üö® JWT ÌÜ†ÌÅ∞ ÏóÜÏùå. Î°úÍ∑∏Ïù∏ ÌïÑÏöî");
          return;
        }

        const response = await axios.get(`http://localhost:8090/api/chat/messages/${member.studentId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });

    

        setMessages(response.data);
      } catch (error) {
        console.error("üö® Ï±ÑÌåÖ ÎÇ¥Ïó≠ÏùÑ Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§.", error);
      }
    }

    fetchMessages();
  }, [member, recipient]);

  // ‚úÖ Ïã§ÏãúÍ∞Ñ Î©îÏãúÏßÄ ÏàòÏã† (Ïù¥Ï†Ñ Î¶¨Ïä§ÎÑà Ï†ïÎ¶¨)
  useEffect(() => {
    const handleMessageReceive = (msg) => {
     

  
      setMessages(prev => {
        if (prev.some(m => m.id === msg.id)) { // ‚úÖ Ï§ëÎ≥µ Î©îÏãúÏßÄ ÌïÑÌÑ∞ÎßÅ
          console.warn("‚ö†Ô∏è Ï§ëÎ≥µ Î©îÏãúÏßÄ Í∞êÏßÄ - Ï∂îÍ∞Ä Ïïà Ìï®", msg);
          return prev;
        }
        return [...prev, msg];
      });
    };
  
    socket.off('chat message');  // ‚úÖ Í∏∞Ï°¥ Ïù¥Î≤§Ìä∏ Ï†úÍ±∞
    socket.on('chat message', handleMessageReceive);  // ‚úÖ ÏÉàÎ°úÏö¥ Î¶¨Ïä§ÎÑà Îì±Î°ù
  
    return () => {
      socket.off('chat message', handleMessageReceive);  // ‚úÖ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ï†ïÎ¶¨
    };
  }, [member, recipient]);
  

  // ‚úÖ Î©îÏãúÏßÄ Ï†ÑÏÜ° Ï≤òÎ¶¨
  const handleSend = async () => {
    const trimmedText = inputText.trim();
    if (!trimmedText || !recipient || !member) return;

    const newMsg = {
      sender: member.studentId,
      recipient: recipient.studentId,
      content: trimmedText
    };

    try {
      const response = await axios.post("http://localhost:8090/api/chat/send", newMsg, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });

      // ‚úÖ UUID ÌôúÏö©ÌïòÏó¨ key Ï§ëÎ≥µ Î¨∏Ï†ú Ìï¥Í≤∞
      setMessages((prev) => [
        ...prev,
        { ...response.data, id: response.data.id || uuidv4() }
      ]);

      socket.emit('chat message', response.data);
      setInputText('');
    } catch (error) {
      console.error("üö® Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®", error);
    }
  };

  return (
    <div className="message-page-container">
      <div className="chat-area">
        <div className="chat-header">
          {member && recipient 
            ? `${member.name}Í≥º ${recipient.name}Ïùò ÎåÄÌôî`
            : "ÏàòÏã†ÏûêÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî"}
        </div>

        <div className="recipient-selector">
          <label>Î∞õÎäî ÏÇ¨Îûå: </label>
          <select onChange={(e) => setRecipient(members.find(m => m.studentId === e.target.value))}>
            <option value="" disabled>üì© Î∞õÎäî ÏÇ¨ÎûåÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî</option>
            {members.map(m => (
              <option key={m.studentId} value={m.studentId}>
                {m.name}
              </option>
            ))}
          </select>
        </div>

        <div className="chat-messages">
          {messages.map((msg) => (
            <div key={msg.id || uuidv4()} // ‚úÖ UUID ÌôúÏö©ÌïòÏó¨ Ï§ëÎ≥µ ÌÇ§ Î∞©ÏßÄ
                 className={`chat-message ${msg.sender === member?.studentId ? 'sent' : 'received'}`}>
              <span>{msg.sender}: </span>
              <span>{msg.content}</span>
            </div>
          ))}
        </div>

        <div className="chat-input">
          <input 
            type="text" 
            value={inputText} 
            onChange={(e) => setInputText(e.target.value)} 
            placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..." 
          />
          <button onClick={handleSend}>Ï†ÑÏÜ°</button>
        </div>
      </div>
    </div>
  );
};

export default MessagePage;
